CREATE TABLE `POSTS` (
  `POST_ID` int(10) NOT NULL,
  `POST_TITLE` varchar(200) DEFAULT NULL,
  `POST_CONTENT` varchar(10000) DEFAULT NULL,
  `POST_DATE` datetime DEFAULT NULL,
  `POST_MODIFIED_DATE` datetime DEFAULT NULL,
  `POST_CATEGORY` varchar(50) DEFAULT NULL,
  `POST_EXPERT` varchar(1000) DEFAULT NULL,
  `POST_TAGS` varchar(100) DEFAULT NULL,
  `POST_VIEWS` int(11) DEFAULT NULL,
  `POST_URI` varchar(100) NOT NULL
);